svg.script:

function drawLocation(id, x, y, statusColor, deviceName, statusText, connectionX, connectionY) {
    var svg = document.getElementById('main_svg');

    // Connection Line
    var line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
    line.setAttribute('x1', 400); line.setAttribute('y1', 300);
    line.setAttribute('x2', connectionX); line.setAttribute('y2', connectionY);
    line.setAttribute('stroke', '#3D5A80'); line.setAttribute('stroke-width', '1');
    svg.appendChild(line);

    // Location Anchor Dot
    var anchor = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
    anchor.setAttribute('cx', connectionX); anchor.setAttribute('cy', connectionY);
    anchor.setAttribute('r', '6'); anchor.setAttribute('fill', statusColor);
    anchor.setAttribute('stroke', '#1A1C20'); anchor.setAttribute('stroke-width', '2');
    svg.appendChild(anchor);

    // Info Box Group
    var group = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    group.setAttribute('transform', 'translate(' + x + ',' + y + ')');

    // Box Background
    var box = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
    box.setAttribute('width', '180'); box.setAttribute('height', '80');
    box.setAttribute('rx', '8'); box.setAttribute('fill', '#1A1C20');
    box.setAttribute('stroke', statusColor); box.setAttribute('stroke-width', '1');
    box.setAttribute('opacity', '0.95');
    group.appendChild(box);

    // Device Name
    var nameText = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    nameText.setAttribute('x', '10'); nameText.setAttribute('y', '20');
    nameText.setAttribute('class', 'text-light');
    nameText.textContent = deviceName;
    group.appendChild(nameText);

    // Status Text
    var statusTextEl = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    statusTextEl.setAttribute('x', '10'); statusTextEl.setAttribute('y', '40');
    statusTextEl.setAttribute('fill', statusColor); statusTextEl.setAttribute('font-family', 'sans-serif');
    statusTextEl.setAttribute('font-size', '14px');
    statusTextEl.textContent = statusText;
    group.appendChild(statusTextEl);

    // IP Address Placeholder
    var ipText = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    ipText.setAttribute('x', '10'); ipText.setAttribute('y', '60');
    ipText.setAttribute('class', 'text-dark'); ipText.setAttribute('font-size', '12px');
    ipText.textContent = 'IP: XXX.XXX.XXX.XXX';
    group.appendChild(ipText);

    // Signal Strength Icon (Simple Arc/Wave)
    var signal = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    signal.setAttribute('transform', 'translate(145, 30)');
    signal.setAttribute('stroke', statusColor); signal.setAttribute('stroke-width', '2');
    signal.setAttribute('fill', 'none');

    // Smallest arc
    var arc1 = document.createElementNS('http://www.w3.org/2000/svg', 'path');
    arc1.setAttribute('d', 'M 0 10 C 5 5, 15 5, 20 10');
    signal.appendChild(arc1);

    // Medium arc
    var arc2 = document.createElementNS('http://www.w3.org/2000/svg', 'path');
    arc2.setAttribute('d', 'M -5 15 C 5 0, 15 0, 25 15');
    signal.appendChild(arc2);

    // Largest arc (faded for lower signal/off)
    if (statusText === 'ONLINE') {
        var arc3 = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        arc3.setAttribute('d', 'M -10 20 C 5 -5, 15 -5, 30 20');
        signal.appendChild(arc3);
    } else if (statusText === 'WARNING') {
         var arc3 = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        arc3.setAttribute('d', 'M -10 20 C 5 -5, 15 -5, 30 20');
        arc3.setAttribute('stroke-opacity', '0.3');
        signal.appendChild(arc3);
    }

    group.appendChild(signal);
    svg.appendChild(group);
}
